import React, {Component} from 'react';
import {Select} from 'react';
import CInstancias from './Componentes/CInstancias.jsx'

const Http = new XMLHttpRequest();
const url='https://l5r8oes3le.execute-api.us-east-1.amazonaws.com/dev';

class Instancias extends Component {
    state = {
        multiplo : [
            { value: 'cris', name: 'Cris' },
            { value: 'troxa', name: 'Troxa' }
        ],
        selecionado: "cris",
        regiaoslc: "us-east-1",
        instanciasAWS: null

    }

    optiontab = () => {
        for(let i = 0; i < this.multiplo.length; i++){
           return <option value={this.multiplo}>{this.multiplo}</option>   
          }
    }


    handleChange = e => {
        this.setState({selecionado: e.selecionado})
    }

    handleRegiao = e => {
        this.setState({regiaoslc: e.target.value})
        console.log(this.state.regiaoslc)

        switch (e.target.value) {
            //chamada da API para criação do Bucket
            case "us-east-1":

                const options ={
                    method: "GET",
                    headers: {"regiao" : "us-east-1"}
                }
                const resultado = document.getElementById('resultado')
                fetch(url, options)
                    .then(resp => resp.json())
                    .then(json => {
                        //console.log(json.body.InstanceStatuses)
                        //const parseOBJ = json1 => JSON.parse(json1)
                        const instanceid = instancias => instancias.InstanceId
                        const instancestatus = instanciasst => instanciasst.InstanceState.Name 
                        const finalI = json.body.InstanceStatuses.map(instanceid)
                        const finalS = json.body.InstanceStatuses.map(instancestatus)
                        console.log(json.body.InstanceStatuses)
                        //resultado.innerHTML = JSON.stringify(finalI)
                        this.setState({instanciasAWS:json.body.InstanceStatuses})

                    })


                break;
            }

    }

    render() {
        return (
        <form className="enviar"
        onSubmit={this.envioAssincrono}>
            <input style ={{fontSize:'19px'}}
            type="text"
            placeholder="Região"
            nome="region" 
            value= {this.state.regiaoslc}
            onChange = {e => this.handleRegiao(e)}
            required
            />
        <div className="container">
            <div className="row">
            <div className="col-md-4"></div>
            <div className="col-md-4">

                <select name="regiao" 
                        value={this.state.selecionado} 
                        onChange={e => this.handleChange(e)}>

                {this.state.multiplo.map((e, key) => {
                    return <option key={key} value={e.value}>{e.name}</option>;
                })}

            </select>

            </div>
            <div className="col-md-4"></div>
            </div>

            { this.state.instanciasAWS ?
            <div>
            {this.state.instanciasAWS.map(ist => {        
                return <CInstancias nome={ist.InstanceId} status={ist.InstanceState.ame}  /> 
            })}  </div> :  null }   
        </div>
                <input type="submit" 
                className="btn"
                style={{fontSize: '19px'}} />           
        </form>
    )}

}
export default Instancias
