import React, {Component} from 'react';
import {Select} from 'react';
const Http = new XMLHttpRequest();
const url='https://l5r8oes3le.execute-api.us-east-1.amazonaws.com/dev';
Http.open("GET", url);

//Http.setRequestHeader("Access-Control-Request-Headers", "*")
//Http.setRequestHeader("Access-Control-Allow-Headers","access-control-allow-methods")
Http.setRequestHeader("Access-Control-Allow-Headers","Content-Type,Access-Control-Allow-Credentials,Authorization, X-PING, Access-Control-Allow-Methods")
Http.setRequestHeader("Access-Control-Allow-Origin","http://site-cris-estatico.s3-website-us-east-1.amazonaws.com")
Http.setRequestHeader("Content-Type", "application/json")
Http.setRequestHeader("Access-Control-Allow-Credentials", "true");
Http.setRequestHeader("Access-Control-Allow-Methods", "GET");


class Instancias extends Component {
    state = {
        multiplo : [
            { value: 'cris', name: 'Cris' },
            { value: 'troxa', name: 'Troxa' }
        ],
        selecionado: "cris",
        regiaoslc: "us-east-1",

    }

    optiontab = () => {
        for(let i = 0; i < this.multiplo.length; i++){
           return <option value={this.multiplo}>{this.multiplo}</option>   
          }
    }


    handleChange = e => {
        this.setState({selecionado: e.selecionado})
    }

    handleRegiao = e => {
        this.setState({regiaoslc: e.target.value})
        console.log(this.state.regiaoslc)

        switch (e.target.value) {
            //chamada da API para criação do Bucket
            case "us-east-1":
                //Http.withCredentials = false
                Http.setRequestHeader('regiao', e.target.value)
                Http.send(null );
                console.log(Http.responseText);    
                break;
            }

    }

    render() {
        return (
        <form className="enviar"
        onSubmit={this.envioAssincrono}>
            <input style ={{fontSize:'19px'}}
            type="text"
            placeholder="Região"
            nome="region" 
            value= {this.state.regiaoslc}
            onChange = {e => this.handleRegiao(e)}
            required
            />
        <div className="container">
            <div className="row">
            <div className="col-md-4"></div>
            <div className="col-md-4">

                <select name="regiao" 
                        value={this.state.selecionado} 
                        onChange={e => this.handleChange(e)}>

                {this.state.multiplo.map((e, key) => {
                    return <option key={key} value={e.value}>{e.name}</option>;
                })}

            </select>

            </div>
            <div className="col-md-4"></div>
            </div>
        </div>
                <input type="submit" 
                className="btn"
                style={{fontSize: '19px'}} />           
        </form>
    )}

}
export default Instancias
